package com.demo.locationwatcher;import android.content.Context;import android.content.SharedPreferences;public class SharedPreferencesManager {    private static SharedPreferencesManager manager;    private SharedPreferences sharedPref;    private SharedPreferencesManager() {    }    private static SharedPreferences getSharedPreferencesInstance() {        if (manager == null) {            manager = new SharedPreferencesManager();            Context ctx = App.getInstance();            manager.sharedPref = ctx.getSharedPreferences("preference_key", Context.MODE_PRIVATE);        }        return manager.sharedPref;    }    public static void deleteAll() {        getSharedPreferencesInstance().edit().clear().commit();    }    /*    Save string data in Sharedpreferences     */    public static void setPreference(String key, String value) {        SharedPreferences.Editor editor = getSharedPreferencesInstance().edit();        editor.putString(key, value);        editor.commit();    }    public static void setPreference(String key, int value) {        SharedPreferences.Editor editor = getSharedPreferencesInstance().edit();        editor.putInt(key, value);        editor.commit();    }    public static void setPreference(String key, boolean value) {        SharedPreferences.Editor editor = getSharedPreferencesInstance().edit();        editor.putBoolean(key, value);        editor.commit();    }    public static void removePreference(String key) {        SharedPreferences.Editor editor = getSharedPreferencesInstance().edit();        editor.remove(key);        editor.commit();    }/*Fetching saved string data from Sharedprefrences */    public static String getStringPreference(String key, String defValue) {        return getSharedPreferencesInstance().getString(key, defValue);    }    public static int getIntPreference(String key, int defValue) {        return getSharedPreferencesInstance().getInt(key, defValue);    }    public static Boolean getBooleanPreference(String key, boolean defValue) {        return getSharedPreferencesInstance().getBoolean(key, defValue);    }}